--[[
 **************************************************************************
 ************* Entry structure ********************************************
 **************************************************************************
{
	-- ---------------= Mandatory fields ------------------------------
	Id = ID                        (int),
	AegisName = "Aegis_Name"       (string),
	Name = "Item Name"             (string),
	-- ---------------= Optional fields ------------------------------=
	Type = Item Type               (string, defaults to "ETC"),
	Buy = Buy Price                (int, defaults to Sell * 2),
	Sell = Sell Price              (int, defaults to Buy ,/ 2)
	Weight = Item Weight           (int, defaults to 0),
	Atk = Attack                   (int, defaults to 0),
	Matk = Magical Attack          (int, defaults to 0, ignored in pre-re),
	Def = Defense                  (int, defaults to 0),
	Range = Attack Range           (int, defaults to 0),
	Slots = Slots                  (int, defaults to 0),
	Job = {                        (defaults to all job)
		All = true
		[ Job.Novice ] = true
		[ Job.Swordman ] = true
		[ Job.Magician ] = true
		[ Job.Archer ] = true
		[ Job.Acolyte ] = true
		[ Job.Merchant ] = true
		[ Job.Thief ] = true
		[ Job.Knight ] = true
		[ Job.Priest ] = true
		[ Job.Wizard ] = true
		[ Job.Blacksmith ] = true
		[ Job.Hunter ] = true
		[ Job.Assassin ] = true
		[ Job.Crusader ] = true
		[ Job.Monk ] = true
		[ Job.Sage ] = true
		[ Job.Rogue ] = true
		[ Job.Alchemist ] = true
		[ Job.Bard ] = true
		[ Job.Taekwon ] = true
		[ Job.StarGladiator ] = true
		[ Job.SoulLinker ] = true
		[ Job.Gunslinger ] = true
		[ Job.Ninja ] = true
		[ Job.Gangsi ] = true
		[ Job.DeathKnight ] = true
		[ Job.DarkCollector ] = true
		[ Job.Kagerou ] = true
		[ Job.Rebllion ] = true
	},
	Job = Job mask                 (alternate format, int, defaults to all jobs ,= 0xFFFFFFFF)
	Gender = Gender                (string, defaults to "IT_GENDER_ANY"),
	Loc = Equip location           (bitmask array, string or int, required value for equipment),
	WeaponLv = Weapon Level        (int, defaults to 0),
	EquipLv = Equip required level (int, defaults to 0),
	EquipLv = { min, max },           (alternative syntax with min / max level)
	Refine = Refineable            (boolean, defaults to true),
	EnableOptions = true,/false     (boolean, defaults to true !!for equipments only!!) [Smokexyz]
	Subtype = Item Subtype         (int, defaults to 0),
	ViewSprite = Sprite view ID    (int, defaults to 0),
	BindOnEquip = true
	ForceSerial = true/false       (boolean, defaults to false)
	Delay = Delay to use item      (int, defaults to 0),
	KeepAfterUse = true
	DropAnnounce = true
	ShowDropEffect = true
	DropEffectMode = Effect Type   (int, default to 0),
	NoTrade = {                      (defaults to no restrictions)
		override = GroupID             (int, defaults to 100),
		ItemTradeMask.Drop
		ItemTradeMask.Trade
		ItemTradeMask.StoreInStorage
		ItemTradeMask.StoreInCart
		ItemTradeMask.SellToNPC
		ItemTradeMask.Mail
		ItemTradeMask.Auction
		ItemTradeMask.StoreInGuildStorage
		partneroverride = true
	},
	RestrictUsage = {                      (defaults to no restrictions)
		override = GroupID             (int, defaults to 100),
		ItemUsageMask.Sitting
	},
	Stack = { amount, type },         (int, defaults to 0)
	Sprite = SpriteID              (int, defaults to 0),
	Script = --[[ (it can be multi-line) --]] --[[

	OnEquipScript = [[ OnEquip Script (can also be multi-line) --]] --[[
	OnUnequipScript = [[ OnUnequip Script (can also be multi-line) --]] --[[
},
**************************************************************************/
--]]

